mapper.delete:
delete
from recipe.like
where user_id = #{userId}
  and recipe_id = #{recipeId}

mapper.select:
select r.recipe_id, r.title, r.introduction, r.ingredients, r.method, r.create_time, r.time_duration, r.user_id, r.is_privacy, count(1) likes from recipe.recipe r left join recipe.like l on r.recipe_id = l.recipe_id left join recipe.tag_recipe tr on tr.recipe_id = r.recipe_id
r.is_privacy = 0
and tr.tag_name in
#{tagName}
group by r.recipe_id;

select file_name
from recipe.photo
where recipe_id = #{recipeId}
